// 用户状态管理
export const userStatusStore = {
  namespaced: true,  // 启用命名空间
  
  state: {
    status: 'online',  // 默认在线状态
    lastActivity: Date.now(),  // 最后活动时间
    onlineUsers: [],  // 在线用户列表
    currentUsername: '',  // 当前用户名
    statusMap: {  // 状态映射
      online: '在线',
      away: '离开',
      busy: '勿扰',
      invisible: '隐身',
      offline: '离线'
    }
  },
  
  mutations: {
    // 设置当前用户名
    SET_CURRENT_USERNAME(state, username) {
      state.currentUsername = username;
    },
    
    // 更新用户状态
    UPDATE_STATUS(state, newStatus) {
      state.status = newStatus;
      state.lastActivity = Date.now();
    },
    
    // 更新最后活动时间
    UPDATE_ACTIVITY(state) {
      state.lastActivity = Date.now();
    },
    
    // 更新在线用户列表
    UPDATE_ONLINE_USERS(state, users) {
      state.onlineUsers = users;
    },

    // 更新头像
    UPDATE_AVATAR(state, avatar) {
      const currentUser = state.onlineUsers.find(user => user.username === state.currentUsername);
      if (currentUser) {
        currentUser.avatar = avatar;
      }
    }
  },
  
  actions: {
    // 初始化用户信息
    initUser({ commit }, username) {
      commit('SET_CURRENT_USERNAME', username);
    },
    
    // 设置用户状态
    async setStatus({ commit }, status) {
      try {
        // 这里可以添加与后端的状态同步逻辑
        commit('UPDATE_STATUS', status);
        
        // 向服务器发送状态更新
        await fetch('http://localhost:3001/api/users/status', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ status })
        });
      } catch (error) {
        console.error('更新状态失败:', error);
      }
    },
    
    // 更新活动状态
    updateActivity({ commit }) {
      commit('UPDATE_ACTIVITY');
    },
    
    // 获取在线用户列表
    async fetchOnlineUsers({ commit }) {
      try {
        const response = await fetch('http://localhost:3001/api/users/online');
        const users = await response.json();
        commit('UPDATE_ONLINE_USERS', users);
      } catch (error) {
        console.error('获取在线用户失败:', error);
      }
    },

    // 更新头像
    async updateAvatar({ commit }, { avatar }) {
      try {
        // 更新本地状态
        commit('UPDATE_AVATAR', avatar);
        
        // 发送到服务器
        await fetch('http://localhost:3001/api/users/avatar', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ avatar })
        });
      } catch (error) {
        console.error('更新头像失败:', error);
      }
    }
  },
  
  getters: {
    // 获取当前状态
    currentStatus: state => state.status,
    
    // 获取状态显示文本
    statusText: state => state.statusMap[state.status] || '离线',
    
    // 获取在线用户列表
    onlineUsers: state => state.onlineUsers,
    
    // 检查用户是否在线
    isUserOnline: state => username => {
      return state.onlineUsers.some(user => user.username === username);
    }
  }
};
